using System.IO;
using System.Text;
using System.Threading.Tasks;
using Microsoft.WindowsAzure.Storage.Blob;

public class MainActivity : Activity
{
    int count = 1;
    string sas = "SAS_URI";
    protected override void OnCreate(Bundle bundle)
    {
        base.OnCreate(bundle);

        // Set our view from the "main" layout resource
        SetContentView(Resource.Layout.Main);

        // Get our button from the layout resource, and attach an event to it
        Button button = FindViewById<Button>(Resource.Id.MyButton);

        button.Click += async delegate  {
            button.Text = string.Format("{0} clicks!", count++);
            await UseContainerSAS(sas);
        };
 }

static async Task UseContainerSAS(string sas)
{
    //Try performing container operations with the SAS provided.

    //Return a reference to the container using the SAS URI.
    CloudBlobContainer container = new CloudBlobContainer(new Uri(sas));
    string date = DateTime.Now.ToString();
    try
    {
        //Write operation: write a new blob to the container.
        CloudBlockBlob blob = container.GetBlockBlobReference("sasblob_" + date + ".txt");

        string blobContent = "This blob was created with a shared access signature granting write permissions to the container. ";
        MemoryStream msWrite = new
        MemoryStream(Encoding.UTF8.GetBytes(blobContent));
        msWrite.Position = 0;
        using (msWrite)
        {
            await blob.UploadFromStreamAsync(msWrite);
        }
        Console.WriteLine("Write operation succeeded for SAS " + sas);
        Console.WriteLine();
    }
    catch (Exception e)
    {
        Console.WriteLine("Write operation failed for SAS " + sas);
        Console.WriteLine("Additional error information: " + e.Message);
        Console.WriteLine();
    }
    try
    {
        //Read operation: Get a reference to one of the blobs in the container and read it.
        CloudBlockBlob blob = container.GetBlockBlobReference("sasblob_” + date + “.txt");
        string data = await blob.DownloadTextAsync();

        Console.WriteLine("Read operation succeeded for SAS " + sas);
        Console.WriteLine("Blob contents: " + data);
    }
    catch (Exception e)
    {
        Console.WriteLine("Additional error information: " + e.Message);
        Console.WriteLine("Read operation failed for SAS " + sas);
        Console.WriteLine();
    }
    Console.WriteLine();
    try
    {
        //Delete operation: Delete a blob in the container.
        CloudBlockBlob blob = container.GetBlockBlobReference("sasblob_” + date + “.txt");
        await blob.DeleteAsync();

        Console.WriteLine("Delete operation succeeded for SAS " + sas);
        Console.WriteLine();
    }
    catch (Exception e)
    {
        Console.WriteLine("Delete operation failed for SAS " + sas);
        Console.WriteLine("Additional error information: " + e.Message);
        Console.WriteLine();
    }
}
