try { # WARNING: Make sure to reference the latest version of Microsoft.ServiceBus.dll Write-Output "Adding the [Microsoft.ServiceBus.dll] assembly to the script..." $scriptPath = Split-Path (Get-Variable MyInvocation -Scope 0).Value.MyCommand.Path $packagesFolder = (Split-Path $scriptPath -Parent) + "\packages" $assembly = Get-ChildItem $packagesFolder -Include "Microsoft.ServiceBus.dll" -Recurse Add-Type -Path $assembly.FullName
Write-Output "The [Microsoft.ServiceBus.dll] assembly has been successfully added to the script." }
catch [System.Exception] { Write-Error("Could not add the Microsoft.ServiceBus.dll assembly to the script. Make sure you build the solution before running the provisioning script.") }

MarkdownFencedCodeBlock.code_block:
$Namespace = "MyServiceBusNS" $Location = "West US"
# Query to see if the namespace currently exists $CurrentNamespace = Get-AzureSBNamespace -Name $Namespace
# Check if the namespace already exists or needs to be created if ($CurrentNamespace) { Write-Output "The namespace [$Namespace] already exists in the [$($CurrentNamespace.Region)] region." } else { Write-Host "The [$Namespace] namespace does not exist." Write-Output "Creating the [$Namespace] namespace in the [$Location] region..." New-AzureSBNamespace -Name $Namespace -Location $Location -CreateACSNamespace $false -NamespaceType Messaging $CurrentNamespace = Get-AzureSBNamespace -Name $Namespace Write-Host "The [$Namespace] namespace in the [$Location] region has been successfully created." }

$Path = "MyEventHub" $PartitionCount = 12 $MessageRetentionInDays = 7 $UserMetadata = $null $ConsumerGroupName = "MyConsumerGroup"
# Check to see if the Event Hub already exists if ($NamespaceManager.EventHubExists($Path)) { Write-Output "The [$Path] event hub already exists in the [$Namespace] namespace." } else { Write-Output "Creating the [$Path] event hub in the [$Namespace] namespace: PartitionCount=[$PartitionCount] MessageRetentionInDays=[$MessageRetentionInDays]..." $EventHubDescription = New-Object -TypeName Microsoft.ServiceBus.Messaging.EventHubDescription -ArgumentList $Path $EventHubDescription.PartitionCount = $PartitionCount $EventHubDescription.MessageRetentionInDays = $MessageRetentionInDays $EventHubDescription.UserMetadata = $UserMetadata $EventHubDescription.Path = $Path $NamespaceManager.CreateEventHubIfNotExists($EventHubDescription); Write-Output "The [$Path] event hub in the [$Namespace] namespace has been successfully created." }
# Create the consumer group if it doesn't exist Write-Output "Creating the consumer group [$ConsumerGroupName] for the [$Path] event hub..." $ConsumerGroupDescription = New-Object -TypeName Microsoft.ServiceBus.Messaging.ConsumerGroupDescription -ArgumentList $Path, $ConsumerGroupName $ConsumerGroupDescription.UserMetadata = $ConsumerGroupUserMetadata $NamespaceManager.CreateConsumerGroupIfNotExists($ConsumerGroupDescription); Write-Output "The consumer group [$ConsumerGroupName] for the [$Path] event hub has been successfully created."
