DROP TABLE ${hiveTableName};
CREATE EXTERNAL TABLE ${hiveTableName}(t1 string, t2 string, t3 string, t4 string, t5 string, t6 string, t7 string) ROW FORMAT DELIMITED FIELDS TERMINATED BY ' ' STORED AS TEXTFILE LOCATION '${hiveDataFolder}';
INSERT OVERWRITE DIRECTORY '${hiveOutputFolder}' SELECT t4 AS sev, COUNT(*) AS cnt FROM ${hiveTableName} WHERE t4 LIKE '[%' GROUP BY t4;

 # Create a storage context object
 $storageaccountkey = get-azurestoragekey $storageAccountName | %{$_.Primary}
 $destContext = New-AzureStorageContext -StorageAccountName $storageAccountName -StorageAccountKey $storageaccountkey

 function uploadOozieFiles()
 {
     Write-Host "Copy HiveQL script, workflow definition and coordinator definition ..." -ForegroundColor Green
     Set-AzureStorageBlobContent -File $hiveQLScript -Container $containerName -Blob "$destFolder/useooziewf.hql" -Context $destContext
     Set-AzureStorageBlobContent -File $workflowDefinition -Container $containerName -Blob "$destFolder/workflow.xml" -Context $destContext
     Set-AzureStorageBlobContent -File $coordDefinition -Container $containerName -Blob "$destFolder/coordinator.xml" -Context $destContext
 }

 function prepareHiveDataFile()
 {
     Write-Host "Make a copy of the sample.log file ... " -ForegroundColor Green
     Start-CopyAzureStorageBlob -SrcContainer $containerName -SrcBlob "example/data/sample.log" -Context $destContext -DestContainer $containerName -destBlob "$destFolder/data/sample.log" -DestContext $destContext
 }

 function prepareSQLDatabase()
 {
     # SQL query string for creating log4jLogsCount table
     $cmdCreateLog4jCountTable = " CREATE TABLE [dbo].[$sqlDatabaseTableName](
             [Level] [nvarchar](10) NOT NULL,
             [Total] float,
         CONSTRAINT [PK_$sqlDatabaseTableName] PRIMARY KEY CLUSTERED
         (
         [Level] ASC
         )
         )"

     #Create the log4jLogsCount table
     Write-Host "Create Log4jLogsCount table ..." -ForegroundColor Green
     $conn = New-Object System.Data.SqlClient.SqlConnection
     $conn.ConnectionString = "Data Source=$sqlDatabaseServer.database.windows.net;Initial Catalog=$sqlDatabaseName;User ID=$sqlDatabaseLogin;Password=$sqlDatabaseLoginPassword;Encrypt=true;Trusted_Connection=false;"
     $conn.open()
     $cmd = New-Object System.Data.SqlClient.SqlCommand
     $cmd.connection = $conn
     $cmd.commandtext = $cmdCreateLog4jCountTable
     $cmd.executenonquery()

     $conn.close()
 }

 # upload workflow.xml, coordinator.xml, and ooziewf.hql
 uploadOozieFiles;

 # make a copy of example/data/sample.log to example/data/log4j/sample.log
 prepareHiveDataFile;

 # create log4jlogsCount table on SQL database
 prepareSQLDatabase;

 function checkOozieServerStatus()
 {
     Write-Host "Checking Oozie server status..." -ForegroundColor Green
     $clusterUriStatus = "https://$clusterName.azurehdinsight.net:443/oozie/v2/admin/status"
     $response = Invoke-RestMethod -Method Get -Uri $clusterUriStatus -Credential $creds -OutVariable $OozieServerStatus

     $jsonResponse = ConvertFrom-Json (ConvertTo-Json -InputObject $response)
     $oozieServerSatus = $jsonResponse[0].("systemMode")
     Write-Host "Oozie server status is $oozieServerSatus..."

     if($oozieServerSatus -notmatch "NORMAL")
     {
         Write-Host "Oozie server status is $oozieServerSatus...cannot submit Oozie jobs. Check the server status and re-run the job."
         exit 1
     }
 }

function createOozieJob() { # create Oozie job Write-Host "Sending the following Payload to the cluster:" -ForegroundColor Green Write-Host "`n--------`n$OoziePayload`n--------" $clusterUriCreateJob = "https://$clusterName.azurehdinsight.net:443/oozie/v2/jobs" $response = Invoke-RestMethod -Method Post -Uri $clusterUriCreateJob -Credential $creds -Body $OoziePayload -ContentType "application/xml" -OutVariable $OozieJobName -debug -Verbose
$jsonResponse = ConvertFrom-Json (ConvertTo-Json -InputObject $response) $oozieJobId = $jsonResponse[0].("id") Write-Host "Oozie job id is $oozieJobId..."
return $oozieJobId }

 function checkOozieJobStatus($oozieJobId)
 {
     # get job status
     Write-Host "Sleeping for $waitTimeBetweenOozieJobStatusCheck seconds until the job metadata is populated in the Oozie metastore..." -ForegroundColor Green
     Start-Sleep -Seconds $waitTimeBetweenOozieJobStatusCheck

     Write-Host "Getting job status and waiting for the job to complete..." -ForegroundColor Green
     $clusterUriGetJobStatus = "https://$clusterName.azurehdinsight.net:443/oozie/v2/job/" + $oozieJobId + "?show=info"
     $response = Invoke-RestMethod -Method Get -Uri $clusterUriGetJobStatus -Credential $creds
     $jsonResponse = ConvertFrom-Json (ConvertTo-Json -InputObject $response)
     $JobStatus = $jsonResponse[0].("status")

     while($JobStatus -notmatch "SUCCEEDED|KILLED")
     {
         Write-Host "$(Get-Date -format 'G'): $oozieJobId is in $JobStatus state...waiting $waitTimeBetweenOozieJobStatusCheck seconds for the job to complete..."
         Start-Sleep -Seconds $waitTimeBetweenOozieJobStatusCheck
         $response = Invoke-RestMethod -Method Get -Uri $clusterUriGetJobStatus -Credential $creds
         $jsonResponse = ConvertFrom-Json (ConvertTo-Json -InputObject $response)
         $JobStatus = $jsonResponse[0].("status")
     }

     Write-Host "$(Get-Date -format 'G'): $oozieJobId is in $JobStatus state!"
     if($JobStatus -notmatch "SUCCEEDED")
     {
         Write-Host "Check logs at http://headnode0:9014/cluster for detais."
         exit -1
     }
 }

 function listOozieJobs()
 {
     Write-Host "Listing Oozie jobs..." -ForegroundColor Green
     $clusterUriStatus = "https://$clusterName.azurehdinsight.net:443/oozie/v2/jobs"
     $response = Invoke-RestMethod -Method Get -Uri $clusterUriStatus -Credential $creds

     write-host "Job ID                                   App Name        Status      Started                         Ended"
     write-host "----------------------------------------------------------------------------------------------------------------------------------"
     foreach($job in $response.workflows)
     {
         Write-Host $job.id "`t" $job.appName "`t" $job.status "`t" $job.startTime "`t" $job.endTime
     }
 }

 function ShowOozieJobLog($oozieJobId)
 {
     Write-Host "Showing Oozie job info..." -ForegroundColor Green
     $clusterUriStatus = "https://$clusterName.azurehdinsight.net:443/oozie/v2/job/$oozieJobId" + "?show=log"
     $response = Invoke-RestMethod -Method Get -Uri $clusterUriStatus -Credential $creds
     write-host $response
 }

 function killOozieJob($oozieJobId)
 {
     Write-Host "Killing the Oozie job $oozieJobId..." -ForegroundColor Green
     $clusterUriStartJob = "https://$clusterName.azurehdinsight.net:443/oozie/v2/job/" + $oozieJobId + "?action=kill" #Valid values for the 'action' parameter are 'start', 'suspend', 'resume', 'kill', 'dryrun', 'rerun', and 'change'.
     $response = Invoke-RestMethod -Method Put -Uri $clusterUriStartJob -Credential $creds | Format-Table -HideTableHeaders -debug
 }
