    public static void CopyBlob(
        [QueueTrigger("copyblobqueue")] string blobName,
        [Blob("textblobs/{queueTrigger}", FileAccess.Read)] Stream blobInput,
        [Blob("textblobs/{queueTrigger}-new", FileAccess.Write)] Stream blobOutput)
