# The path of this script SCRIPT_PATH="$( dirname "${BASH_SOURCE[0]}" )" # Charmrun command CHARMRUN=${SCRIPT_PATH}/charmrun # Argument of ++nodelist NODELIST_OPT="++nodelist" # Argument of ++p NUMPROCESS="+p"
# Get node information from ENVs # CCP_NODES_CORES=3 CENTOS66LN-00 4 CENTOS66LN-01 4 CENTOS66LN-03 4 NODESCORES=(${CCP_NODES_CORES}) COUNT=${#NODESCORES[@]}
if [ ${COUNT} -eq 0 ] then # If CCP_NODES_CORES is not found or is empty, just run the charmrun without nodelist arg. #echo ${CHARMRUN} $* ${CHARMRUN} $* else # Create the nodelist file NODELIST_PATH=${SCRIPT_PATH}/nodelist_$$
# Write the head line echo "group main" > ${NODELIST_PATH}
# Get every node name & cores and write into the nodelist file I=1 while [ ${I} -lt ${COUNT} ] do echo "host ${NODESCORES[${I}]} ++cpus ${NODESCORES[$(($I+1))]}" >> ${NODELIST_PATH} let "I=${I}+2" done
# Run the charmrun with nodelist arg #echo ${CHARMRUN} ${NUMPROCESS}${CCP_NUMCPUS} ${NODELIST_OPT} ${NODELIST_PATH} $* ${CHARMRUN} ${NUMPROCESS}${CCP_NUMCPUS} ${NODELIST_OPT} ${NODELIST_PATH} $*
RTNSTS=$? rm -f ${NODELIST_PATH} fi
exit ${RTNSTS}
