MarkdownCodeBlock.code_block:
 #region - variables and constants
 $clusterName = "<HDInsightClusterName>" # Enter the HDInsight cluster name

 # Enter the OAuth information for your Twitter application
 $oauth_consumer_key = "<TwitterAppConsumerKey>";
 $oauth_consumer_secret = "<TwitterAppConsumerSecret>";
 $oauth_token = "<TwitterAppAccessToken>";
 $oauth_token_secret = "<TwitterAppAccessTokenSecret>";

 $destBlobName = "tutorials/twitter/data/tweets.txt" # This script saves the tweets into this blob.

 $trackString = "Azure, Cloud, HDInsight" # This script gets the tweets containing these keywords.
 $track = [System.Uri]::EscapeDataString($trackString);
 $lineMax = 10000  # The script will get this number of tweets. It is about 3 minutes every 100 lines.
 #endregion

 #region - Connect to Azure subscription
 Write-Host "`nConnecting to your Azure subscription ..." -ForegroundColor Green
 Add-AzureAccount
 #endregion

 #region - Create a block blob object for writing tweets into Blob storage
 Write-Host "Get the default storage account name and Blob container name using the cluster name ..." -ForegroundColor Green
 $myCluster = Get-AzureHDInsightCluster -Name $clusterName
 $storageAccountName = $myCluster.DefaultStorageAccount.StorageAccountName.Replace(".blob.core.windows.net", "")
 $containerName = $myCluster.DefaultStorageAccount.StorageContainerName
 Write-Host "`tThe storage account name is $storageAccountName." -ForegroundColor Yellow
 Write-Host "`tThe blob container name is $containerName." -ForegroundColor Yellow

 Write-Host "Define the Azure storage connection string ..." -ForegroundColor Green
 $storageAccountKey = get-azurestoragekey $storageAccountName | %{$_.Primary}
 $storageConnectionString = "DefaultEndpointsProtocol=https;AccountName=$storageAccountName;AccountKey=$storageAccountKey"
 Write-Host "`tThe connection string is $storageConnectionString." -ForegroundColor Yellow

 Write-Host "Create block blob object ..." -ForegroundColor Green
 $storageAccount = [Microsoft.WindowsAzure.Storage.CloudStorageAccount]::Parse($storageConnectionString)
 $storageClient = $storageAccount.CreateCloudBlobClient();
 $storageContainer = $storageClient.GetContainerReference($containerName)
 $destBlob = $storageContainer.GetBlockBlobReference($destBlobName)
 #end region

 # region - Format OAuth strings
 Write-Host "Format oauth strings ..." -ForegroundColor Green
 $oauth_nonce = [System.Convert]::ToBase64String([System.Text.Encoding]::ASCII.GetBytes([System.DateTime]::Now.Ticks.ToString()));
 $ts = [System.DateTime]::UtcNow - [System.DateTime]::ParseExact("01/01/1970", "dd/MM/yyyy", $null)
 $oauth_timestamp = [System.Convert]::ToInt64($ts.TotalSeconds).ToString();

 $signature = "POST&";
 $signature += [System.Uri]::EscapeDataString("https://stream.twitter.com/1.1/statuses/filter.json") + "&";
 $signature += [System.Uri]::EscapeDataString("oauth_consumer_key=" + $oauth_consumer_key + "&");
 $signature += [System.Uri]::EscapeDataString("oauth_nonce=" + $oauth_nonce + "&");
 $signature += [System.Uri]::EscapeDataString("oauth_signature_method=HMAC-SHA1&");
 $signature += [System.Uri]::EscapeDataString("oauth_timestamp=" + $oauth_timestamp + "&");
 $signature += [System.Uri]::EscapeDataString("oauth_token=" + $oauth_token + "&");
 $signature += [System.Uri]::EscapeDataString("oauth_version=1.0&");
 $signature += [System.Uri]::EscapeDataString("track=" + $track);

 $signature_key = [System.Uri]::EscapeDataString($oauth_consumer_secret) + "&" + [System.Uri]::EscapeDataString($oauth_token_secret);

 $hmacsha1 = new-object System.Security.Cryptography.HMACSHA1;
 $hmacsha1.Key = [System.Text.Encoding]::ASCII.GetBytes($signature_key);
 $oauth_signature = [System.Convert]::ToBase64String($hmacsha1.ComputeHash([System.Text.Encoding]::ASCII.GetBytes($signature)));

 $oauth_authorization = 'OAuth ';
 $oauth_authorization += 'oauth_consumer_key="' + [System.Uri]::EscapeDataString($oauth_consumer_key) + '",';
 $oauth_authorization += 'oauth_nonce="' + [System.Uri]::EscapeDataString($oauth_nonce) + '",';
 $oauth_authorization += 'oauth_signature="' + [System.Uri]::EscapeDataString($oauth_signature) + '",';
 $oauth_authorization += 'oauth_signature_method="HMAC-SHA1",'
 $oauth_authorization += 'oauth_timestamp="' + [System.Uri]::EscapeDataString($oauth_timestamp) + '",'
 $oauth_authorization += 'oauth_token="' + [System.Uri]::EscapeDataString($oauth_token) + '",';
 $oauth_authorization += 'oauth_version="1.0"';

 $post_body = [System.Text.Encoding]::ASCII.GetBytes("track=" + $track);
 #endregion

 #region - Read tweets
 Write-Host "Create HTTP web request ..." -ForegroundColor Green
 [System.Net.HttpWebRequest] $request = [System.Net.WebRequest]::Create("https://stream.twitter.com/1.1/statuses/filter.json");
 $request.Method = "POST";
 $request.Headers.Add("Authorization", $oauth_authorization);
 $request.ContentType = "application/x-www-form-urlencoded";
 $body = $request.GetRequestStream();

 $body.write($post_body, 0, $post_body.length);
 $body.flush();
 $body.close();
 $response = $request.GetResponse() ;

 Write-Host "Start stream reading ..." -ForegroundColor Green

 Write-Host "Define a MemoryStream and a StreamWriter for writing ..." -ForegroundColor Green
 $memStream = New-Object System.IO.MemoryStream
 $writeStream = New-Object System.IO.StreamWriter $memStream

 $sReader = New-Object System.IO.StreamReader($response.GetResponseStream())

 $inrec = $sReader.ReadLine()
 $count = 0
 while (($inrec -ne $null) -and ($count -le $lineMax))
 {
     if ($inrec -ne "")
     {
         Write-Host "`n`t $count tweets received." -ForegroundColor Yellow

         $writeStream.WriteLine($inrec)
         $count ++
     }

     $inrec=$sReader.ReadLine()
 }
 #endregion

 #region - Write tweets to Blob storage
 Write-Host "Write to the destination blob ..." -ForegroundColor Green
 $writeStream.Flush()
 $memStream.Seek(0, "Begin")
 $destBlob.UploadFromStream($memStream)

 $sReader.close()
 #endregion

 Write-Host "Completed!" -ForegroundColor Green
