#Create Virtual Network
$vnetStr = @"<?xml version="1.0" encoding="utf-8"?> <NetworkConfiguration xmlns:xsd="http://www.w3.org/2001/XMLSchema" xmlns:xsi="http://www.w3.org/2001/XMLSchema-instance" xmlns="http://schemas.microsoft.com/ServiceHosting/2011/07/NetworkConfiguration"> <VirtualNetworkConfiguration> <VirtualNetworkSites> <VirtualNetworkSite name="[your-vnet-name]" Location="West US"> <AddressSpace> <AddressPrefix>[your-address-prefix]</AddressPrefix> </AddressSpace> <Subnets> <Subnet name="[your-subnet-name]"> <AddressPrefix>[your-subnet-range]</AddressPrefix> </Subnet> </Subnets> </VirtualNetworkSite> </VirtualNetworkSites> </VirtualNetworkConfiguration> </NetworkConfiguration> "@;
$vnetConfigPath = "<path-to-vnet-config>" Set-AzureVNetConfig -ConfigurationPath $vnetConfigPath;

#Initialize domain variables

$domain = ‘<your-domain-name>’;
$dmuser = ‘$domain\<your-username>’;
$dmpswd = '<your-domain-password>';
$dmspwd = ConvertTo-SecureString $dmpswd -AsPlainText -Force;
$dmcred = New-Object System.Management.Automation.PSCredential ($dmuser, $dmspwd);

#Add AD Domain Extension to the cloud service roles

Set-AzureServiceADDomainExtension -Service <your-cloud-service-hosted-service-name> -Role <your-role-name> -Slot <staging-or-production> -DomainName $domain -Credential $dmcred -JoinOption 35;
