create database if not exists nyctaxidb;
create external table if not exists nyctaxidb.trip ( medallion string, hack_license string, vendor_id string, rate_code string, store_and_fwd_flag string, pickup_datetime string, dropoff_datetime string, passenger_count int, trip_time_in_secs double, trip_distance double, pickup_longitude double, pickup_latitude double, dropoff_longitude double, dropoff_latitude double) PARTITIONED BY (month int) ROW FORMAT DELIMITED FIELDS TERMINATED BY ',' lines terminated by '\n' STORED AS TEXTFILE LOCATION 'wasb:///nyctaxidbdata/trip' TBLPROPERTIES('skip.header.line.count'='1');
create external table if not exists nyctaxidb.fare ( medallion string, hack_license string, vendor_id string, pickup_datetime string, payment_type string, fare_amount double, surcharge double, mta_tax double, tip_amount double, tolls_amount double, total_amount double) PARTITIONED BY (month int) ROW FORMAT DELIMITED FIELDS TERMINATED BY ',' lines terminated by '\n' STORED AS TEXTFILE LOCATION 'wasb:///nyctaxidbdata/fare' TBLPROPERTIES('skip.header.line.count'='1');

SELECT tip_class, COUNT(*) AS tip_freq 
FROM 
(
    SELECT if(tip_amount=0, 0, 
        if(tip_amount>0 and tip_amount<=5, 1, 
        if(tip_amount>5 and tip_amount<=10, 2, 
        if(tip_amount>10 and tip_amount<=20, 3, 4)))) as tip_class, tip_amount
    FROM nyctaxidb.fare
)tc
GROUP BY tip_class;

set R=3959; set pi=radians(180);
insert overwrite directory 'wasb:///queryoutputdir'
select pickup_longitude, pickup_latitude, dropoff_longitude, dropoff_latitude, trip_distance, trip_time_in_secs, ${hiveconf:R}*2*2*atan((1-sqrt(1-pow(sin((dropoff_latitude-pickup_latitude) *${hiveconf:pi}/180/2),2)-cos(pickup_latitude*${hiveconf:pi}/180) *cos(dropoff_latitude*${hiveconf:pi}/180)*pow(sin((dropoff_longitude-pickup_longitude)*${hiveconf:pi}/180/2),2))) /sqrt(pow(sin((dropoff_latitude-pickup_latitude)*${hiveconf:pi}/180/2),2) +cos(pickup_latitude*${hiveconf:pi}/180)*cos(dropoff_latitude*${hiveconf:pi}/180)* pow(sin((dropoff_longitude-pickup_longitude)*${hiveconf:pi}/180/2),2))) as direct_distance from nyctaxidb.trip where month=1 and pickup_longitude between -90 and -30 and pickup_latitude between 30 and 90 and dropoff_longitude between -90 and -30 and dropoff_latitude between 30 and 90;
