function add(req, res) {
    var result = parseInt(req.query.a) + parseInt(req.query.b);
    res.send(200, { result: result });
}

function subtract(req, res) {
    var result = parseInt(req.query.a) - parseInt(req.query.b);
    res.send(200, { result: result });
}

