var email = new sendgrid.Email({
    to: 'john@contoso.com',
    from: 'anna@contoso.com',
    subject: 'test mail',
    text: 'This is a sample email message.'
});

sendgrid.send(email, function(err, json){
    if(err) { return console.error(err); }
    console.log(json);
});

