using System.IO;        
using System.Threading;
using System.Globalization;
using Microsoft.HBase.Client;
using Tweetinvi.Core.Interfaces;
using org.apache.hadoop.hbase.rest.protobuf.generated;

public class DictionaryItem
{
    public string Type { get; set; }
    public int Length { get; set; }
    public string Word { get; set; }
    public string Pos { get; set; }
    public string Stemmed { get; set; }
    public string Polarity { get; set; }
}

 // HDinsight HBase cluster and HBase table information
 const string CLUSTERNAME = "https://<HBaseClusterName>.azurehdinsight.net/";
 const string HADOOPUSERNAME = "<HadoopUserName>"; //the default name is "admin"
 const string HADOOPUSERPASSWORD = "<HaddopUserPassword>";
 const string HBASETABLENAME = "tweets_by_words";

 // Sentiment dictionary file and the punctuation characters
 const string DICTIONARYFILENAME = @"..\..\data\dictionary\dictionary.tsv";
 private static char[] _punctuationChars = new[] { 
     ' ', '!', '\"', '#', '$', '%', '&', '\'', '(', ')', '*', '+', ',', '-', '.', '/',   //ascii 23--47
     ':', ';', '<', '=', '>', '?', '@', '[', ']', '^', '_', '`', '{', '|', '}', '~' };   //ascii 58--64 + misc.

 // For writting to HBase
 HBaseClient client;

 // a sentiment dictionary for estimate sentiment. It is loaded from a physical file.
 Dictionary<string, DictionaryItem> dictionary;

 // use multithread write
 Thread writerThread;
 Queue<ITweet> queue = new Queue<ITweet>();
 bool threadRunning = true;

 // This function connects to HBase, loads the sentiment dictionary, and starts the thread for writting.
 public HBaseWriter()
 {
     ClusterCredentials credentials = new ClusterCredentials(new Uri(CLUSTERNAME), HADOOPUSERNAME, HADOOPUSERPASSWORD);
     client = new HBaseClient(credentials);

     // create the HBase table if it doesn't exist
     if (!client.ListTables().name.Contains(HBASETABLENAME))
     {
         TableSchema tableSchema = new TableSchema();
         tableSchema.name = HBASETABLENAME;
         tableSchema.columns.Add(new ColumnSchema { name = "d" });
         client.CreateTable(tableSchema);
         Console.WriteLine("Table \"{0}\" is created.", HBASETABLENAME);
     }

     // Load sentiment dictionary from a file
     LoadDictionary();

     // Start a thread for writting to HBase
     writerThread = new Thread(new ThreadStart(WriterThreadFunction));
     writerThread.Start();
 }

 ~HBaseWriter()
 {
     threadRunning = false;
 }

 // Enqueue the Tweets received
 public void WriteTweet(ITweet tweet)
 {
     lock (queue)
     {
         queue.Enqueue(tweet);
     }
 }

 // Load sentiment dictionary from a file
 private void LoadDictionary()
 {
     List<string> lines = File.ReadAllLines(DICTIONARYFILENAME).ToList();
     var items = lines.Select(line =>
     {
         var fields = line.Split('\t');
         var pos = 0;
         return new DictionaryItem
         {
             Type = fields[pos++],
             Length = Convert.ToInt32(fields[pos++]),
             Word = fields[pos++],
             Pos = fields[pos++],
             Stemmed = fields[pos++],
             Polarity = fields[pos++]
         };
     });

     dictionary = new Dictionary<string, DictionaryItem>();
     foreach (var item in items)
     {
         if (!dictionary.Keys.Contains(item.Word))
         {
             dictionary.Add(item.Word, item);
         }
     }
 }

 // Calculate sentiment score
 private int CalcSentimentScore(string[] words)
 {
     Int32 total = 0;
     foreach (string word in words)
     {
         if (dictionary.Keys.Contains(word))
         {
             switch (dictionary[word].Polarity)
             {
                 case "negative": total -= 1; break;
                 case "positive": total += 1; break;
             }
         }
     }
     if (total > 0)
     {
         return 1;
     }
     else if (total < 0)
     {
         return -1;
     }
     else
     {
         return 0;
     }
 }

 // Popular a CellSet object to be written into HBase
 private void CreateTweetByWordsCells(CellSet set, ITweet tweet)
 {
     // Split the Tweet into words
     string[] words = tweet.Text.ToLower().Split(_punctuationChars);

     // Calculate sentiment score base on the words
     int sentimentScore = CalcSentimentScore(words);
     var word_pairs = words.Take(words.Length - 1)
                           .Select((word, idx) => string.Format("{0} {1}", word, words[idx + 1]));
     var all_words = words.Concat(word_pairs).ToList();

     // For each word in the Tweet add a row to the HBase table
     foreach (string word in all_words)
     {
         string time_index = (ulong.MaxValue - (ulong)tweet.CreatedAt.ToBinary()).ToString().PadLeft(20) + tweet.IdStr;
         string key = word + "_" + time_index;

         // Create a row
         var row = new CellSet.Row { key = Encoding.UTF8.GetBytes(key) };

         // Add columns to the row, including Tweet identifier, language, coordinator(if available), and sentiment 
         var value = new Cell { column = Encoding.UTF8.GetBytes("d:id_str"), data = Encoding.UTF8.GetBytes(tweet.IdStr) };
         row.values.Add(value);

         value = new Cell { column = Encoding.UTF8.GetBytes("d:lang"), data = Encoding.UTF8.GetBytes(tweet.Language.ToString()) };
         row.values.Add(value);

         if (tweet.Coordinates != null)
         {
             var str = tweet.Coordinates.Longitude.ToString() + "," + tweet.Coordinates.Latitude.ToString();
             value = new Cell { column = Encoding.UTF8.GetBytes("d:coor"), data = Encoding.UTF8.GetBytes(str) };
             row.values.Add(value);
         }

         value = new Cell { column = Encoding.UTF8.GetBytes("d:sentiment"), data = Encoding.UTF8.GetBytes(sentimentScore.ToString()) };
         row.values.Add(value);

         set.rows.Add(row);
     }
 }

 // Write a Tweet (CellSet) to HBase
 public void WriterThreadFunction()
 {
     try
     {
         while (threadRunning)
         {
             if (queue.Count > 0)
             {
                 CellSet set = new CellSet();
                 lock (queue)
                 {
                     do
                     {
                         ITweet tweet = queue.Dequeue();
                         CreateTweetByWordsCells(set, tweet);
                     } while (queue.Count > 0);
                 }

                 // Write the Tweet by words cell set to the HBase table
                 client.StoreCells(HBASETABLENAME, set);
                 Console.WriteLine("\tRows written: {0}", set.rows.Count);
             }
             Thread.Sleep(100);
         }
     }
     catch (Exception ex)
     {
         Console.WriteLine("Exception: " + ex.Message);
     }
 }

